mapper.select:

 			select * from (
					select @rownum:=@rownum+1 as rownum, b.* 
					from (select @rownum:=0) as tmp, post_reply as b 
					where bno = #{bno}
			 		order by rnogroup desc, bundel_order asc
				) post_reply
				where rownum > (#{cri.pageNum}-1)* #{cri.amount} 
				and rownum <= #{cri.pageNum}* #{cri.amount};
